 '&#116;', $cop2);			$cop4 = str_replace('u', '&#117;', $cop3);			$cop5 = str_replace('v', '&#118;', $cop4);			$cop6 = str_replace('w', '&#119;', $cop5);			$cop8 = str_replace('x', '&#120;', $cop6);			$cop9 = str_replace('y', '&#121;', $cop8);			$cop10 = str_replace('z', '&#122;', $cop9);			$cop11 = str_replace('@', '&#64;', $cop10);			$cop12 = str_replace('.', '&#46;', $cop11);			$cop13 = str_replace('_', '&#95;', $cop12);			$finall = trim($cop13);			return $finall;		}else{return "";}	}}//desencriptarclass encriptar2{	function encrip($encrip)	{		if (!is_null($encrip))	{			$a = str_replace('&#97;', 'a', $encrip);			$e = str_replace('&#98;', 'b', $a);			$i = str_replace('&#99;', 'c', $e);			$o = str_replace('&#100;', 'd', $i);			$u = str_replace('&#101;', 'e', $o);			$n = str_replace('&#102;', 'f', $u);			$A = str_replace('&#103;', 'g', $n);			$E = str_replace('&#104;', 'h', $A);			$I = str_replace('&#105;', 'i', $E);			$O = str_replace('&#106;', 'j', $I);			$U = str_replace('&#107;', 'k', $O);			$N = str_replace('&#108;', 'l', $U);			$milla = str_replace('&#109;', 'm', $N);			$milla1 = str_replace('&#110;', 'n', $milla);			$guio1 = str_replace('&#111;', 'o', $milla1);			$guio2 = str_replace('&#112;', 'p', $guio1);			$guio3 = str_replace('&#113;', 'q', $guio2);			$cop1 = str_replace('&#114;', 'r', $guio3);			$cop2 = str_replace('&#115;', 's', $cop1);			$cop3 = str_replace('&#116;', 't', $cop2);			$cop4 = str_replace('&#117;', 'u', $cop3);			$cop5 = str_replace('&#118;', 'v', $cop4);			$cop6 = str_replace('&#119;', 'w', $cop5);			$cop8 = str_replace('&#120;', 'x', $cop6);			$cop9 = str_replace('&#121;', 'y', $cop8);			$cop10 = str_replace('&#122;', 'z', $cop9);			$cop11 = str_replace('&#64;', '@', $cop10);			$cop12 = str_replace('&#46;', '.', $cop11);			$cop13 = str_replace('&#95;', '_', $cop12);			$finall = trim($cop13);			return $finall;		}else{return "";}	}}//CODIFICAR�class codix{	function codifx($encripf)	{		if (!is_null($encripf))	{			$a = str_replace( chr(225), '&aacute;',$encripf);			$e = str_replace(chr(233), '&eacute;', $a);			$i = str_replace(chr(237),'&iacute;',  $e);			$o = str_replace(chr(243),'&oacute;',  $i);			$u = str_replace(chr(250),'&uacute;',  $o);			$n = str_replace( chr(241),'&ntilde;', $u);			$A = str_replace( chr(193),'&Aacute;', $n);			$E = str_replace(chr(201), '&Eacute;', $A);			$I = str_replace(chr(205),'&Iacute;',  $E);			$O = str_replace( chr(211),'&Oacute;', $I);			$U = str_replace( chr(218),'&Uacute;', $O);			$N = str_replace(chr(209),'&Ntilde;',  $U);			$preg = str_replace( chr(447), '&iquest;',$N);			$guio1 = str_replace( chr(171),'&laquo;', $preg);			$guio2 = str_replace(chr(443),'&raquo;',  $guio1);			$guio3 = str_replace(chr(417), '&iexcl;', $guio2);			$cop1 = str_replace(chr(425), '&copy;', $guio3);			$cop2 = str_replace(chr(470),'&Ouml;',  $cop1);			$cop3 = str_replace( chr(246), '&ouml;',$cop2);			$cop4 = str_replace(chr(276),'&Uuml;',  $cop3);			$cop5 = str_replace(chr(252),'&uuml;',  $cop4);			$cop6 = str_replace(chr(174),'&reg;',  $cop5);			$cop7 = str_replace(chr(155),'&oslash;;',  $cop6);			$cop8 = str_replace(chr(157),'&Oslash;;',  $cop7);			$preg1 = str_replace(chr(177), '&plusmn;', $cop8);			$finall = trim($preg1);			return $finall;		}else{return "";}	}}//decodificarclass decodificar{	function decodif($decodif){	    if (!is_null($decodif)){			$a = str_replace('&aacute;', chr(225), $decodif);			$e = str_replace('&eacute;', chr(233), $a);			$i = str_replace('&iacute;', chr(237), $e);			$o = str_replace('&oacute;', chr(243), $i);			$u = str_replace('&uacute;', chr(250), $o);			$n = str_replace('&ntilde;', chr(241), $u);			$A = str_replace('&Aacute;', chr(193), $n);			$E = str_replace('&Eacute;', chr(201), $A);			$I = str_replace('&Iacute;', chr(205), $E);			$O = str_replace('&Oacute;', chr(211), $I);			$U = str_replace('&Uacute;', chr(218), $O);			$N = str_replace('&Ntilde;', chr(209), $U);			$preg = str_replace('&iquest;', chr(447), $N);			$guio1 = str_replace('&laquo;', chr(171), $preg);			$guio2 = str_replace('&raquo;', chr(443), $guio1);			$guio3 = str_replace('&iexcl;', chr(417), $guio2);			$cop1 = str_replace('&copy;', chr(425), $guio3);			$cop2 = str_replace('&Ouml;', chr(470), $cop1);			$cop3 = str_replace('&ouml;', chr(246), $cop2);			$cop4 = str_replace('&Uuml;', chr(276), $cop3);			$cop5 = str_replace('&uuml;', chr(252), $cop4);			$cop6 = str_replace('&reg;', chr(174), $cop5);			$cop7 = str_replace('&amp;', chr(550), $cop6);			$cop8 = str_replace('&lt;', chr(572), $cop7);			$cop9 = str_replace('&gt;', chr(574), $cop8);			$cop10 = str_replace('&plusmn;', chr(177), $cop9);			$cop11 = str_replace('&nbsp;', ' ', $cop10);			$cop15 = str_replace('&middot;', chr(45), $cop11);			$preg1 = str_replace('&quot;', chr(290), $cop15);			$finallx = trim($preg1);			return $finallx;	    }else{			return "";	    }	}}//decodificaryclass decodificary{	function decodify($decodif){	    if (!is_null($decodif)){			$a = str_replace('&aacute;', "�", $decodif);			$e = str_replace('&eacute;', "�", $a);			$i = str_replace('&iacute;', "�", $e);			$o = str_replace('&oacute;', "�", $i);			$u = str_replace('&uacute;', "�", $o);			$n = str_replace('&ntilde;', "�", $u);			$A = str_replace('&Aacute;', "�", $n);			$E = str_replace('&Eacute;', "�", $A);			$I = str_replace('&Iacute;', "�", $E);			$O = str_replace('&Oacute;', "�", $I);			$U = str_replace('&Uacute;', "�", $O);			$N = str_replace('&Ntilde;', "L", $U);			$cop4 = str_replace('&Uuml;', "�", $N);			$cop5 = str_replace('&uuml;', "�", $cop4);			$cop7 = str_replace('&amp;', "&", $cop5);			$preg1 = str_replace(';', "", $cop7);			$finallx = trim($preg1);			return $finallx;	    }else{			return "";	    }	}}class DECO{	function decox($encripf)	{		if (!is_null($encripf))	{			$a = str_replace( chr(225), 'a',$encripf);			$e = str_replace(chr(233), 'e', $a);			$i = 